private function isPasswordValid($password)
{
    // Check if the password contains consecutive and/or repeated numbers
    if (preg_match('/(\\d)\\1+/', $password) || preg_match('/123|234|345|456|567|678|789/', $password)) {
        return false;
    }

    return true;
}

