public static void bubbleSort(int[] numbers) {
  System.out.printf("Unsorted array in Java :%s %n", Arrays.toString(numbers));
  for (int i = 0; i < numbers.length; i++) {
    for (int j = numbers.length -1; j > i; j--) {
      if (numbers[j] < numbers[j - 1]) {
        swap(numbers, j, j-1);
      }
    }
  }
