  validates :email, presence: true, uniqueness: { case_sensitive: false }, format: { without: /\\+/ }
end

class User
  private
  attr_writer :name, :email, :password

  public
  def name
    @name
  end

  def email
    @email
  end

  def password
    @password
  end

  def name=(value)
    @name = value
  end

  def email=(value)
    @email = value
  end

  def password=(value)
    @password = value
  end
end

